import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import Image from '../assets/LogInGirl.png'
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel'


const useStyles = makeStyles((theme) => ({
	root: {
		height: '100vh'
	},
	image: {
		backgroundImage: {Image},
		backgroundRepeat: 'no-repeat',
		backgroundSize: 'cover',
		backgroundPosition: 'center'
	},
	paper: {
		margin: theme.spacing(8, 4),
		display: 'flex',
		flexDirection: 'column',
		alignItems: 'center'
	},
	avatar: {
		margin: theme.spacing(1),
		backgroundColor: '#99B27F'
	},
	form: {
		width: '100%', // Fix IE 11 issue.
		marginTop: theme.spacing(1)
	},
	submit: {
        margin: theme.spacing(3, 0, 2),
        color: '#ffffff',
        backgroundColor: '#D48883'
    },
}));

const LoginComponent = ({ login, isAuthenticated }) => {
	const classes = useStyles();
	const [ data, setData ] = useState(null);
	function handleSubmit(e) {
		e.preventDefault();

    const { role, email, password } = data;
    login(role, email, password);
	}

	return (
		<Grid container component="main" className={classes.root}>
			<CssBaseline />
			<Grid item xs={false} sm={4} md={7} className={classes.image} />
			<Grid item xs={10} sm={6} md={3} component={Paper} elevation={0} square>
				<div className={classes.paper}>
					<Avatar className={classes.avatar}>
						<LockOutlinedIcon />
					</Avatar>
					<Typography component="h1" variant="h5">
						Welcome Back!
					</Typography>
					<form className={classes.form} onSubmit={handleSubmit} noValidate>
						<TextField
							margin="normal"
							required
                            fullWidth
							id="email"
							label="Email Address"
							name="email"
							autoComplete="email"
							autoFocus
							onChange={(e) => {
								setData({
									...data,
									email: e.target.value
								});
							}}
						/>
						<TextField
							margin="normal"
							required
                            fullWidth
							name="password"
							label="Password"
							type="password"
							id="password"
							autoComplete="current-password"
							onChange={(e) => {
								setData({
									...data,
									password: e.target.value
								});
							}}
						/>
            <Grid item xs={12}>
						</Grid>
						<Button type="submit" style = {{borderRadius: 0}} fullWidth variant="contained" className={classes.submit}>
							Sign In
						</Button>
						<Grid container>
							<Grid item>
								<Link href="/signup" variant="body2">
									{"Don't have an account? Sign Up"}
								</Link>
							</Grid>
						</Grid>
					</form>
				</div>
			</Grid>
		</Grid>
	);
}

export default (LoginComponent);
